import { atomic, UIComponent } from "MPageFusion";
import { SAVE_CONFIG } from "../actions";

/**
 * Creates the save config button.
 * @class SaveConfigButton
 */
class SaveConfigButton extends UIComponent {
    /**
     * @inheritDoc
     */
    propChangeHandlers() {
        return {
            display: (display) => this.getChild("button").setProp("display", display),
            disabled: (disabled) => this.getChild("button").setProp("disabled", disabled)
        };
    }

    /**
     * @inheritDoc
     */
    createChildren() {
        return [
            {
                button: new atomic.button.Button({
                    clickEventName: SAVE_CONFIG,
                    display: "Save Configuration"
                })
            }
        ]
    }

    /**
     * @inheritDoc
     */
    view(el, props, children, mapped) {
        return this.renderChildren();
    }
}

export default SaveConfigButton;
