import { atomic, UIComponent } from "MPageFusion";
import { runCommand, routeUrl } from "Gaia";
import i18n from "../../i18n/en_us";
import "../../styles/UnitTester.less";

const {
    DocumentRenderer
} = atomic;

/**
 * Displays the code coverage of the unit test suite.
 * @class Coverage
 */
export default class Coverage extends UIComponent {

    /**
     * @inheritDoc
     */
    createChildren() {
        return [
            {
                docRend: new DocumentRenderer({
                    src: `${routeUrl("unit-testing", "coverage")}/index.html`
                })
            }
        ];
    }

    /**
     * @inheritDoc
     */
    view(el, props, children, mappedChildren) {
        return el("div", {class: "coverage-view"}, mappedChildren.docRend.render());
    }
}
