"use strict";

const merge = require("webpack-merge");
const path = require("path");

const withLocalI18n = function (config) {
    return merge(config, {
        resolve: {
            alias: {
                i18n: path.resolve(__dirname, "../../../src/main/i18n/cerner/mpagedev/mpagefusion/index.js")
            }
        }
    });
};

const withGlobalI18n = function (config) {
    return merge(config, {
        resolve: {
            alias: {
                i18n: path.resolve(__dirname, "../../../src/main/i18n/cerner/mpagedev/mpagefusion/globali18n.js")
            }
        }
    });
};

module.exports = {
    withLocalI18n,
    withGlobalI18n
};
