"use strict";

import Button from "./Button";

// ------------------------------------------------------------------------------
// Constants
// ------------------------------------------------------------------------------
const SelectButtonEvents = {
    SELECTION_CHANGE: "SelectButton::selectionChange"
};

/**
 * The SelectButton class.
 * @class SelectButton
 */
class SelectButton extends Button {
    /**
     * @inheritDoc
     */
    initialProps() {
        return Object.assign(super.initialProps(), {
            selectionChangeEventName: SelectButtonEvents.SELECTION_CHANGE
        });
    }

    /**
     * Handles the click event.
     * @returns {undefined} Returns nothing.
     * @private
     */
    _handleClick() {
        const isSelected = !this.getProp("isSelected");
        this.setProp("isSelected", isSelected);

        this.emit(this.getProp("selectionChangeEventName"), this, isSelected);
        this.update();
    }
}

SelectButton.EVENTS = SelectButtonEvents;

export default SelectButton;
