"use strict";

import UIComponent from "../../../base";

export default class Container extends UIComponent {
    /**
     * @inheritdoc
     */
    initialProps() {
        return {
            wrapChildren: false,
            classNames: ""
        };
    }

    /**
     * @inheritdoc
     */
    view(el, props, children, mChildren) {
        if (props.wrapChildren) {
            return el("div", {
                class: props.classNames
            }, this.renderChildren());
        }
        else {
            return this.renderChildren();
        }
    }
}
