"use strict";

import SvgIcon from "../../SvgIcon";
import { triangle, exclamation } from "../../SvgIconDefinitions";
import { colors } from "../../helpers/const";

export default class Alert extends SvgIcon {
    /**
     * @inheritDoc
     */
    get _name() {
        return "Alert";
    }

    /**
     * @inheritDoc
     */
    getRenderedBody(el) {
        return [
            el("path", {
                d: triangle,
                fill: colors.alert
            }, []),
            el("path", {
                d: exclamation,
                fill: colors.white
            }, [])
        ];
    }
}
