"use strict";

import SvgIcon from "../../SvgIcon";
import { dash } from "../../SvgIconDefinitions";
import { colors } from "../../helpers/const";

export default class Error extends SvgIcon {
    /**
     * @inheritDoc
     */
    get _name() {
        return "Error";
    }

    /**
     * @inheritDoc
     */
    getRenderedBody(el) {
        return [
            el("circle", {
                cx: "24",
                cy: "24",
                r: "24",
                fill: colors.alert
            }, []),
            el("path", {
                d: dash,
                fill: colors.white
            }, [])
        ];
    }
}
