"use strict";

import SvgIcon from "../../SvgIcon";
import { caretRight } from "../../svgDefinitions";

/**
 * The Collapsed icon class constructor
 * @class Collapsed
 * @extends SvgIcon
 */
export default class Collapsed extends SvgIcon {
    /**
     * @inheritDoc
     */
    get _name() {
        return "Collapsed";
    }

    /**
     * @inheritDoc
     */
    initialProps() {
        const props = super.initialProps();
        return Object.assign(props, {
            defaultClassNames: `${props.defaultClassNames} mpageui-SvgIcon--small`
        });
    }

    /**
     * @inheritDoc
     */
    getSvg() {
        return caretRight;
    }
}
