"use strict";

import SvgIcon from "../../SvgIcon";
import { caretDown } from "../../svgDefinitions";

/**
 * The Expanded icon class constructor
 * @class Expanded
 * @extends SvgIcon
 */
export default class Expanded extends SvgIcon {
    /**
     * @inheritDoc
     */
    get _name() {
        return "Expanded";
    }

    /**
     * @inheritDoc
     */
    initialProps() {
        const props = super.initialProps();
        return Object.assign(props, {
            defaultClassNames: `${props.defaultClassNames} mpageui-SvgIcon--small`
        });
    }

    /**
     * @inheritDoc
     */
    getSvg() {
        return caretDown;
    }
}
