"use strict";

import UIComponent from "../../../base";
import i18n from "i18n";
import classNames from "classnames";

const { noResultsFound } = i18n();
const NAMESPACE = "mpageui-NoResults";

/**
 * The NoResults class, typically used to indicate that a data retrieval returned no
 * results.
 * @class NoResults
 */
export default class NoResults extends UIComponent {
    /**
     * @inheritDoc
     */
    initialProps() {
        return {
            display: noResultsFound
        };
    }
    /**
     * @inheritDoc
     */
    view(el, props) {
        return el(
            "div",
            {
                class: classNames(NAMESPACE)
            },
            props.display
        );
    }
}
