"use strict";

import UIComponent from "../../../base";

/**
 * @class ProgressBar
 * @extends {UIComponent}
 */
class ProgressBar extends UIComponent {
    /**
     * @inheritDoc
     */
    initialProps() {
        return {
            value: 0,
            progressBarColor: ""
        };
    }

    /**
     * @inheritDoc
     */
    view(el, props, children) {
        return el(
            "div",
            {
                class: "mpageui-ProgressBar"
            },
            el(
                "div",
                {
                    class: `${props.progressBarColor}`,
                    style: {
                        width: `${props.value * 100}%`,
                        height: "100%"
                    }
                }
            )
        );
    }
}
export default ProgressBar;
