"use strict";

import UIComponent from "../../../base";

export default class ProxyRenderer extends UIComponent {
    /**
     * @inheritdoc
     */
    initialProps() {
        return {
            content: [],
            wrapChildren: false,
            classNames: ""
        };
    }

    /**
     * @inheritdoc
     */
    view(el, props) {
        if (props.wrapChildren) {
            return el("div", {
                class: props.classNames
            }, props.content.map((content) => content.render()));
        }
        else {
            return props.content.map((content) => content.render());
        }
    }
}
