"use strict";

import UIComponent from "../../../../base";
import classNames from "classnames";

/**
 * The CollapseGroup Divider class.
 * @class Divider
 */
class Divider extends UIComponent {
    /**
     * @inheritDoc
     */
    view(el) {
        return el(
            "div",
            {
                class: classNames("mpageui-CollapseGroup-divider")
            },
            []
        );
    }
}

/**
 * Creates the Menu divider.
 * @returns { { isDivider: true } } The divider Menu item.
 */
export const makeMenuItem = () => ({
    isDivider: true
});

/**
 * Creates the face-up Divider for the CollapseGroup.
 * @returns {Divider} The face-up Divider.
 */
export const makeFaceUp = () => new Divider();
