"use strict";

import UIComponent from "../../../../base";
import { DataSection } from "../../../atomic/data";

/**
 * The DetailPanelData class.
 * @class DetailPanelData
 */
class DetailPanelData extends UIComponent {
    /**
     * @inheritDoc
     */
    initialProps() {
        return {
            classNames: "",
            sections: []
        };
    }

    /**
     * @inheritDoc
     */
    propChangeHandlers() {
        return {
            sections: (sections) => {
                this.getChild("dataSection").setProp("sections", sections);
            }
        };
    }

    /**
     * @inheritDoc
     */
    createChildren() {
        return [
            {
                dataSection: new DataSection()
            }
        ];
    }

    /**
     * @inheritDoc
     */
    view(el, props, children, mappedChildren) {
        return mappedChildren.dataSection.render();
    }
}

DetailPanelData.LABEL_POSITION = DataSection.LABEL_POSITION;

export default DetailPanelData;
