import { LoadingSpinner } from "../../atomic/indicator";
import { NAMESPACE } from "./manager/const";

/**
 * Gets a loading spinner for the search functionality.
 * @returns {LoadingSpinner} Loading spinner instance.
 */
const getLoadingSpinner = () => new LoadingSpinner({
    classNames: `${NAMESPACE}-loading`,
    isLoading: true
});

export {
    getLoadingSpinner
};
