"use strict";

/**
 * Checks if the browser is IE or edge.
 * @param {boolean} [includeEdge = true] - boolean value to indicate if function should check for Edge or not
 * @returns {boolean} True if the browser is IE or edge
 */
export const isIE = (includeEdge = true) => {
    const isIERegex = includeEdge ? /(MSIE|Edge|Trident\/7.0)/gi : /(MSIE|Trident\/7.0)/gi;
    return isIERegex.test(navigator.userAgent);
};
