"use strict";

/**
 * The base z-index as defined in the 'mpageui-disclosure-element' css class.
 * @type {number}
 */
const BASE_Z_INDEX = 1100;

/**
 * Updates the z-index of the disclosure elements to preserve stacking order.
 * @returns {undefined} This function doesn't return anything
 */
export const updateZIndexes = () => {
    const disclosureElements = document.getElementsByClassName("mpageui-disclosure-element");
    const lastIndex = disclosureElements.length - 1;
    if (lastIndex > 0) { // If there are at least two disclosure elements
        // Set the z-index of the last disclosure element to greater than the z-index of the previous disclosure element
        disclosureElements[lastIndex].style.zIndex = BASE_Z_INDEX + (lastIndex * 10);
    }
};
