"use strict";

/* global describe: false, it: false */

var assert = require("assert"),
    GraphVertex = require(global.MODULE_ROOT + "/libraries/scripts/Bender/DataContainer/GraphVertex");

describe("Bender/DataContainer/GraphVertex", function () {
    it("can reject some values", function () {
        var graphVertex = new GraphVertex();

        graphVertex.isItemAllowed = function (item) {
            return item < 5;
        };

        graphVertex.value = 4;
        assert.throws(function () {
            graphVertex.value = 5;
        });
    });
});
