"use strict";

/* global describe: false, it: false */

var assert = require("assert");

describe("event-dispatcher", function () {

    it("returns every package class", function () {
        var edModule = require(global.MODULE_ROOT + "/libraries/modules/event-dispatcher");

        assert.ok(edModule.Event);
        assert.ok(edModule.EventAggregator);
        assert.ok(edModule.EventDispatcher);
        assert.ok(edModule.EventDispatcherRepository);
    });

});
