const readConfig = (gaia, args) =>
    gaia.runCommand(
        "config",
        "read",
        Object.assign(args, {
            plugin: "injector"
        })
    ).then((config) => config || {});

const saveConfig = (gaia, args, config) =>
    gaia.runCommand(
        "config",
        "write",
        {
            config: config,
            plugin: "injector"
        }
    );

module.exports = {
    readConfig,
    saveConfig
}