import generateUnifiedDriver from "./parseUnifiedDriver";
import parseCriterionJson from "./parseCriterionJson";
import parseUnifiedOrgDriver from "./parseUnifiedOrgDriver";
import parseCriterionJsonForOrgDriver from "./parseCriterionJsonForOrgDriver"

/**
 * Based on the parser type, retrieves the assocated param
 * parser.
 * @param {String} type - The parser type.
 * @returns {Function} The parser function.
 */
const getParser = (type) => ({
    "unified_driver": generateUnifiedDriver,
    "criterion": parseCriterionJson,
    "unified_org_driver": parseUnifiedOrgDriver,
    "criterion_org": parseCriterionJsonForOrgDriver
})[ type ];

export default getParser;
