/* globals jest */

require("@babel/polyfill");

// Inject createSpyObj as jest doesn't provide it's implementation
jasmine.createSpyObj = (baseName, methodNames) => {
    const obj = {};

    for (let i = 0; i < methodNames.length; i++) {
        obj[methodNames[i]] = jasmine.createSpy();
    }

    return obj;
};

// Polyfill for matchMedia for JSDOM
window.matchMedia = window.matchMedia || (() => {
    return {
        matches: true,
        addListener: () => {},
        removeListener: () => {}
    };
});
