"use strict";

import SvgIcon from "../../SvgIcon";
import { check } from "../../SvgIconDefinitions";
import { colors } from "../../helpers/const";

export default class Success extends SvgIcon {
    /**
     * @inheritDoc
     */
    get _name() {
        return "Success";
    }

    /**
     * @inheritDoc
     */
    getRenderedBody(el) {
        return [
            el("circle", {
                cx: "24",
                cy: "24",
                r: "24",
                fill: colors.green
            }, []),
            el("path", {
                d: check,
                fill: colors.white
            }, [])
        ];
    }
}
