"use strict";

import SvgIcon from "../../SvgIcon";
import {
    triangle,
    exclamation,
    triangleBorder
} from "../../SvgIconDefinitions";
import { colors } from "../../helpers/const";

export default class Warning extends SvgIcon {
    /**
     * @inheritDoc
     */
    get _name() {
        return "Warning";
    }

    /**
     * @inheritDoc
     */
    getRenderedBody(el) {
        return [
            el("path", {
                d: triangle,
                fill: colors.warning
            }, []),
            el("path", {
                d: triangleBorder,
                fill: colors.black
            }, []),
            el("path", {
                d: exclamation,
                fill: colors.black
            }, [])
        ];
    }
}
