import UIComponent from "../../../base";
import classNames from "classnames";

const NAMESPACE = "mpageui-Label";

/**
 * Basic label class.
 * @class Label
 */
export default class Label extends UIComponent {
    /**
     * @inheritdoc
     */
    initialProps() {
        return {
            classNames: "",
            value: ""
        };
    }

    /**
     * @inheritDoc
     */
    view(el, props) {
        return el("span", {
            class: classNames(NAMESPACE, {
                [ props.classNames ]: props.classNames.length > 0
            })
        }, props.value);
    }
}
