"use strict";

import UIComponent from "../../../base";

const NAMESPACE = "mpageui-Slider";

/**
 * The Slider class.
 * @class Slider
 */
class Slider extends UIComponent {
    /**
     * @inheritDoc
     */
    initialProps() {
        return {
            value: "",
            requestChangeEventName: null, // Required
            isDisabled: false
        };
    }

    view(el, props, children) {
        return el(
            "input",
            {
                type: "range",
                value: props.value,
                onchange: (e) => {
                    this.emit(props.requestChangeEventName, this, {
                        value: e.target.value
                    });
                },
                disabled: props.isDisabled,
                class: NAMESPACE
            },
            []
        );
    }
}

export default Slider;
