"use strict";

import UIComponent from "../../../base";

export default class TabPanel extends UIComponent {
    /**
     * @inheritDoc
     */
    initialProps() {
        return {
            content: [],
            isSelected: false
        };
    }

    /**
     * @inheritDoc
     */
    propChangeHandlers() {
        return {
            content: (content) => (content && content.length) ? this.replaceAllChildren(content) : []
        };
    }

    /**
     * @inheritDoc
     */
    view(el, props, children, mChildren) {
        return props.isSelected ? el("div", { key: props.id }, this.renderChildren()) : null;
    }
}
