import { PillBox } from "../../../atomic/badge";
import { PRIVATE_EVENTS } from "./const";

/**
 * Handles the prop change for the list of tags.
 * @param {TagList} control - The TagList instance.
 * @returns {function} Handler for the tags prop.
 */
const tagsChange = (control) => (tags) => {
    control.replaceAllChildren(tags.map((t) => new PillBox({
        display: t.display,
        key: t.key,
        requestDeleteEventName: PRIVATE_EVENTS.PILLBOX_REQUEST_DELETE
    })));
};

export {
    tagsChange
};
