import UIComponent from "../../../base";

/**
 * The ViewpointDropdownContent class.
 * @class ViewpointDropdownContent
 */
export default class ViewpointDropdownContent extends UIComponent {
    /**
     * @inheritDoc
     */
    initialProps() {
        return {
            display: "",
            icon: null
        };
    }

    /**
     * @inheritDoc
     */
    view(el, props, children) {
        return el(
            "div", {
                class: "mpageui-ViewpointDropdown-content"
            },
            [
                el(
                    "span",
                    {
                        title: props.display,
                        class: "mpageui-ViewpointDropdown-display"
                    },
                    props.display
                ),
                props.icon ? props.icon.render() : null
            ]
        );
    }
}
