module.exports = function(api, opts) {
    api.assertVersion(7);

    return {
        presets : [
            [ require("@babel/preset-env"), opts ]
        ],
        plugins : [
            /**
             * These plugins already exist in preset-env.
             * Re-specified to supply additional options.
             */
            [ require("@babel/plugin-proposal-object-rest-spread"), { loose: true } ],
            [ require("@babel/plugin-transform-classes"), { loose: true } ],
            [ require("@babel/plugin-transform-computed-properties"), { loose: true } ],
            [ require("@babel/plugin-transform-modules-commonjs"), { loose: true } ],
            [ require("@babel/plugin-transform-parameters"), { loose: true } ],
            [ require("@babel/plugin-transform-template-literals"), { loose: true } ]
        ]
    }    
};