/**
 * Returns the parameters for an executing request
 * @param  {Request} request Request being executed
 * @return {String} String containing the parameters being executed
 */
const getRequestParameters = (request) => {
    return request.query.parameters
        || (request.body && request.body.parameters)
        || "";
}

/**
 * Returns the data blob for an executing request
 * @param  {Request} request Request being executed
 * @return {String} String containing the data blob sent with the request
 */
const getRequestBlobIn = (request) => {
    return request.query.blobIn
        || (request.body && request.body.blobIn)
        || "";
}

module.exports = {
    getRequestParameters,
    getRequestBlobIn
};